   103→            await self.startRecognition()
   104→            self.phase = .listening
   105→            await MainActor.run { TalkModeController.shared.updatePhase(.listening) }
   106→            self.startSilenceMonitor()
   107→        }
   108→    }
   109→
   110→    private func isCurrent(_ generation: Int) -> Bool {
   111→        generation == self.lifecycleGeneration && self.isEnabled
   112→    }
   113→
   114→    private func start() async {
   115→        let gen = self.lifecycleGeneration
   116→        guard voiceWakeSupported else { return }
   117→        guard PermissionManager.voiceWakePermissionsGranted() else {
   118→            self.logger.debug("talk runtime not starting: permissions missing")
   119→            return
   120→        }
   121→        await self.reloadConfig()
   122→        guard self.isCurrent(gen) else { return }
   123→        if self.isPaused {
   124→            self.phase = .idle
   125→            await MainActor.run {
   126→                TalkModeController.shared.updateLevel(0)
   127→                TalkModeController.shared.updatePhase(.idle)
   128→            }
   129→            return
   130→        }
   131→        await self.startRecognition()
   132→        guard self.isCurrent(gen) else { return }
   133→        self.phase = .listening
   134→        await MainActor.run { TalkModeController.shared.updatePhase(.listening) }
   135→        self.startSilenceMonitor()
   136→    }
