   138→    private func stop() async {
   139→        self.captureTask?.cancel()
   140→        self.captureTask = nil
   141→        self.silenceTask?.cancel()
   142→        self.silenceTask = nil
   143→
   144→        // Stop audio before changing phase (stopSpeaking is gated on .speaking).
   145→        await self.stopSpeaking(reason: .manual)
   146→
   147→        self.lastTranscript = ""
   148→        self.lastHeard = nil
   149→        self.lastSpeechEnergyAt = nil
   150→        self.phase = .idle
   151→        await self.stopRecognition()
   152→        await MainActor.run {
   153→            TalkModeController.shared.updateLevel(0)
   154→            TalkModeController.shared.updatePhase(.idle)
   155→        }
   156→    }
