   158→    // MARK: - Speech recognition
   159→
   160→    private struct RecognitionUpdate {
   161→        let transcript: String?
   162→        let hasConfidence: Bool
   163→        let isFinal: Bool
   164→        let errorDescription: String?
   165→        let generation: Int
   166→    }
   167→
   168→    private func startRecognition() async {
   169→        await self.stopRecognition()
   170→        self.recognitionGeneration &+= 1
   171→        let generation = self.recognitionGeneration
   172→
   173→        let locale = await MainActor.run { AppStateStore.shared.voiceWakeLocaleID }
   174→        self.recognizer = SFSpeechRecognizer(locale: Locale(identifier: locale))
   175→        guard let recognizer, recognizer.isAvailable else {
   176→            self.logger.error("talk recognizer unavailable")
   177→            return
   178→        }
   179→
   180→        self.recognitionRequest = SFSpeechAudioBufferRecognitionRequest()
   181→        self.recognitionRequest?.shouldReportPartialResults = true
   182→        guard let request = self.recognitionRequest else { return }
   183→
   184→        if self.audioEngine == nil {
   185→            self.audioEngine = AVAudioEngine()
   186→        }
   187→        guard let audioEngine = self.audioEngine else { return }
   188→
   189→        let input = audioEngine.inputNode
   190→        let format = input.outputFormat(forBus: 0)
   191→        input.removeTap(onBus: 0)
   192→        let meter = self.rmsMeter
   193→        input.installTap(onBus: 0, bufferSize: 2048, format: format) { [weak request, meter] buffer, _ in
   194→            request?.append(buffer)
   195→            if let rms = Self.rmsLevel(buffer: buffer) {
   196→                meter.set(rms)
   197→            }
   198→        }
   199→
   200→        audioEngine.prepare()
   201→        do {
   202→            try audioEngine.start()
   203→        } catch {
   204→            self.logger.error("talk audio engine start failed: \(error.localizedDescription, privacy: .public)")
   205→            return
   206→        }
   207→
   208→        self.startRMSTicker(meter: meter)
   209→
   210→        self.recognitionTask = recognizer.recognitionTask(with: request) { [weak self, generation] result, error in
   211→            guard let self else { return }
   212→            let segments = result?.bestTranscription.segments ?? []
   213→            let transcript = result?.bestTranscription.formattedString
   214→            let update = RecognitionUpdate(
   215→                transcript: transcript,
   216→                hasConfidence: segments.contains { $0.confidence > 0.6 },
   217→                isFinal: result?.isFinal ?? false,
   218→                errorDescription: error?.localizedDescription,
   219→                generation: generation)
   220→            Task { await self.handleRecognition(update) }
   221→        }
   222→    }
   223→
   224→    private func stopRecognition() async {
   225→        self.recognitionGeneration &+= 1
   226→        self.recognitionTask?.cancel()
   227→        self.recognitionTask = nil
   228→        self.recognitionRequest?.endAudio()
   229→        self.recognitionRequest = nil
   230→        self.audioEngine?.inputNode.removeTap(onBus: 0)
   231→        self.audioEngine?.stop()
   232→        self.audioEngine = nil
   233→        self.recognizer = nil
   234→        self.rmsTask?.cancel()
   235→        self.rmsTask = nil
   236→    }
   237→
   238→    private func startRMSTicker(meter: RMSMeter) {
   239→        self.rmsTask?.cancel()
   240→        self.rmsTask = Task { [weak self, meter] in
   241→            while let self {
   242→                try? await Task.sleep(nanoseconds: 50_000_000)
   243→                if Task.isCancelled { return }
   244→                await self.noteAudioLevel(rms: meter.get())
   245→            }
   246→        }
   247→    }
   248→
   249→    private func handleRecognition(_ update: RecognitionUpdate) async {
   250→        guard update.generation == self.recognitionGeneration else { return }
   251→        guard !self.isPaused else { return }
   252→        if let errorDescription = update.errorDescription {
   253→            self.logger.debug("talk recognition error: \(errorDescription, privacy: .public)")
   254→        }
   255→        guard let transcript = update.transcript else { return }
   256→
   257→        let trimmed = transcript.trimmingCharacters(in: .whitespacesAndNewlines)
   258→        if self.phase == .speaking, self.interruptOnSpeech {
   259→            if await self.shouldInterrupt(transcript: trimmed, hasConfidence: update.hasConfidence) {
   260→                await self.stopSpeaking(reason: .speech)
   261→                self.lastTranscript = ""
   262→                self.lastHeard = nil
   263→                await self.startListening()
   264→            }
   265→            return
   266→        }
   267→
   268→        guard self.phase == .listening else { return }
   269→
   270→        if !trimmed.isEmpty {
   271→            self.lastTranscript = trimmed
   272→            self.lastHeard = Date()
   273→        }
   274→
   275→        if update.isFinal {
   276→            self.lastTranscript = trimmed
   277→        }
   278→    }
   279→
   280→    // MARK: - Silence handling
   281→
   282→    private func startSilenceMonitor() {
   283→        self.silenceTask?.cancel()
   284→        self.silenceTask = Task { [weak self] in
   285→            await self?.silenceLoop()
   286→        }
   287→    }
   288→
   289→    private func silenceLoop() async {
   290→        while self.isEnabled {
   291→            try? await Task.sleep(nanoseconds: 200_000_000)
   292→            await self.checkSilence()
   293→        }
   294→    }
   295→
   296→    private func checkSilence() async {
   297→        guard !self.isPaused else { return }
   298→        guard self.phase == .listening else { return }
   299→        let transcript = self.lastTranscript.trimmingCharacters(in: .whitespacesAndNewlines)
   300→        guard !transcript.isEmpty else { return }
   301→        guard let lastHeard else { return }
   302→        let elapsed = Date().timeIntervalSince(lastHeard)
   303→        guard elapsed >= self.silenceWindow else { return }
   304→        await self.finalizeTranscript(transcript)
   305→    }
   306→
   307→    private func startListening() async {
   308→        self.phase = .listening
   309→        self.lastTranscript = ""
   310→        self.lastHeard = nil
   311→        await MainActor.run {
   312→            TalkModeController.shared.updatePhase(.listening)
   313→            TalkModeController.shared.updateLevel(0)
   314→        }
   315→    }
   316→
   317→    private func finalizeTranscript(_ text: String) async {
   318→        self.lastTranscript = ""
   319→        self.lastHeard = nil
   320→        self.phase = .thinking
   321→        await MainActor.run { TalkModeController.shared.updatePhase(.thinking) }
   322→        await self.stopRecognition()
   323→        await self.sendAndSpeak(text)
   324→    }
