   326→    // MARK: - Gateway + TTS
   327→
   328→    private func sendAndSpeak(_ transcript: String) async {
   329→        let gen = self.lifecycleGeneration
   330→        await self.reloadConfig()
   331→        guard self.isCurrent(gen) else { return }
   332→        let prompt = self.buildPrompt(transcript: transcript)
   333→        let activeSessionKey = await MainActor.run { WebChatManager.shared.activeSessionKey }
   334→        let sessionKey: String = if let agentId = self.talkAgentId {
   335→            "agent:\(agentId):main"
   336→        } else if let activeSessionKey {
   337→            activeSessionKey
   338→        } else {
   339→            await GatewayConnection.shared.mainSessionKey()
   340→        }
   341→        let runId = UUID().uuidString
   342→        let startedAt = Date().timeIntervalSince1970
   343→        self.logger.info(
   344→            "talk send start runId=\(runId, privacy: .public) " +
   345→                "session=\(sessionKey, privacy: .public) " +
   346→                "chars=\(prompt.count, privacy: .public)")
   347→
   348→        do {
   349→            let response = try await GatewayConnection.shared.chatSend(
   350→                sessionKey: sessionKey,
   351→                message: prompt,
   352→                thinking: "low",
   353→                idempotencyKey: runId,
   354→                attachments: [])
   355→            guard self.isCurrent(gen) else { return }
   356→            self.logger.info(
   357→                "talk chat.send ok runId=\(response.runId, privacy: .public) " +
   358→                    "session=\(sessionKey, privacy: .public)")
   359→
   360→            guard let assistantText = await self.waitForAssistantText(
   361→                sessionKey: sessionKey,
   362→                since: startedAt,
   363→                timeoutSeconds: 45)
   364→            else {
   365→                self.logger.warning("talk assistant text missing after timeout")
   366→                await self.startListening()
   367→                await self.startRecognition()
   368→                return
   369→            }
   370→            guard self.isCurrent(gen) else { return }
   371→
   372→            self.logger.info("talk assistant text len=\(assistantText.count, privacy: .public)")
   373→            await self.playAssistant(text: assistantText)
   374→            guard self.isCurrent(gen) else { return }
   375→            await self.resumeListeningIfNeeded()
   376→            return
   377→        } catch {
   378→            self.logger.error("talk chat.send failed: \(error.localizedDescription, privacy: .public)")
   379→            await self.resumeListeningIfNeeded()
   380→            return
   381→        }
   382→    }
   383→
   384→    private func resumeListeningIfNeeded() async {
   385→        if self.isPaused {
   386→            self.lastTranscript = ""
   387→            self.lastHeard = nil
   388→            self.lastSpeechEnergyAt = nil
   389→            await MainActor.run {
   390→                TalkModeController.shared.updateLevel(0)
   391→            }
   392→            return
   393→        }
   394→        await self.startListening()
   395→        await self.startRecognition()
   396→    }
