   398→    private func buildPrompt(transcript: String) -> String {
   399→        let interrupted = self.lastInterruptedAtSeconds
   400→        self.lastInterruptedAtSeconds = nil
   401→        return TalkPromptBuilder.build(transcript: transcript, interruptedAtSeconds: interrupted)
   402→    }
   403→
   404→    private func waitForAssistantText(
   405→        sessionKey: String,
   406→        since: Double,
   407→        timeoutSeconds: Int) async -> String?
   408→    {
   409→        let deadline = Date().addingTimeInterval(TimeInterval(timeoutSeconds))
   410→        while Date() < deadline {
   411→            if let text = await self.latestAssistantText(sessionKey: sessionKey, since: since) {
   412→                return text
   413→            }
   414→            try? await Task.sleep(nanoseconds: 300_000_000)
   415→        }
   416→        return nil
   417→    }
   418→
   419→    private func latestAssistantText(sessionKey: String, since: Double? = nil) async -> String? {
   420→        do {
   421→            let history = try await GatewayConnection.shared.chatHistory(sessionKey: sessionKey)
   422→            let messages = history.messages ?? []
   423→            let decoded: [OpenClawChatMessage] = messages.compactMap { item in
   424→                guard let data = try? JSONEncoder().encode(item) else { return nil }
   425→                return try? JSONDecoder().decode(OpenClawChatMessage.self, from: data)
   426→            }
   427→            let assistant = decoded.last { message in
   428→                guard message.role == "assistant" else { return false }
   429→                guard let since else { return true }
   430→                guard let timestamp = message.timestamp else { return false }
   431→                return TalkHistoryTimestamp.isAfter(timestamp, sinceSeconds: since)
   432→            }
   433→            guard let assistant else { return nil }
   434→            let text = assistant.content.compactMap(\.text).joined(separator: "\n")
   435→            let stripped = Self.stripDirectiveTags(text)
   436→            let trimmed = stripped.trimmingCharacters(in: CharacterSet.whitespacesAndNewlines)
   437→            return trimmed.isEmpty ? nil : trimmed
   438→        } catch {
   439→            self.logger.error("talk history fetch failed: \(error.localizedDescription, privacy: .public)")
   440→            return nil
   441→        }
   442→    }
   443→
   444→    /// Strip openclaw inline directive tags (e.g. [[reply_to_current]], [[audio_as_voice]])
   445→    /// before sending text to TTS so they are not spoken aloud.
   446→    private static func stripDirectiveTags(_ text: String) -> String {
   447→        // Matches [[reply_to_current]], [[reply_to: <id>]], [[audio_as_voice]]
   448→        let pattern = #"\[\[\s*(?:reply_to_current|reply_to\s*:[^\]\n]+|audio_as_voice)\s*\]\]"#
   449→        guard let regex = try? NSRegularExpression(pattern: pattern, options: .caseInsensitive) else {
   450→            return text
   451→        }
   452→        let range = NSRange(text.startIndex..., in: text)
   453→        return regex.stringByReplacingMatches(in: text, range: range, withTemplate: "")
   454→            .replacingOccurrences(of: "  ", with: " ")
   455→    }
   456→
   457→    private func playAssistant(text: String) async {
   458→        guard let input = await self.preparePlaybackInput(text: text) else { return }
   459→        do {
   460→            if self.ttsBaseUrl != nil {
   461→                try await self.playOpenAITTS(input: input)
   462→            } else if let apiKey = input.apiKey, !apiKey.isEmpty, let voiceId = input.voiceId {
   463→                try await self.playElevenLabs(input: input, apiKey: apiKey, voiceId: voiceId)
   464→            } else {
   465→                try await self.playSystemVoice(input: input)
   466→            }
   467→        } catch {
   468→            self.ttsLogger
   469→                .error(
   470→                    "talk TTS failed: \(error.localizedDescription, privacy: .public); " +
   471→                        "falling back to system voice")
   472→            do {
   473→                try await self.playSystemVoice(input: input)
   474→            } catch {
   475→                self.ttsLogger.error("talk system voice failed: \(error.localizedDescription, privacy: .public)")
   476→            }
   477→        }
   478→
   479→        if self.phase == .speaking {
   480→            self.phase = .thinking
   481→            await MainActor.run { TalkModeController.shared.updatePhase(.thinking) }
   482→        }
   483→    }
