   485→    private struct TalkPlaybackInput {
   486→        let generation: Int
   487→        let cleanedText: String
   488→        let directive: TalkDirective?
   489→        let apiKey: String?
   490→        let voiceId: String?
   491→        let language: String?
   492→        let synthTimeoutSeconds: Double
   493→    }
   494→
   495→    private func preparePlaybackInput(text: String) async -> TalkPlaybackInput? {
   496→        let gen = self.lifecycleGeneration
   497→        let parse = TalkDirectiveParser.parse(text)
   498→        let directive = parse.directive
   499→        let cleaned = parse.stripped.trimmingCharacters(in: .whitespacesAndNewlines)
   500→        guard !cleaned.isEmpty else { return nil }
   501→        guard self.isCurrent(gen) else { return nil }
   502→
   503→        if !parse.unknownKeys.isEmpty {
   504→            self.logger
   505→                .warning(
   506→                    "talk directive ignored keys: " +
   507→                        "\(parse.unknownKeys.joined(separator: ","), privacy: .public)")
   508→        }
   509→
   510→        let requestedVoice = directive?.voiceId?.trimmingCharacters(in: .whitespacesAndNewlines)
   511→        let resolvedVoice = self.resolveVoiceAlias(requestedVoice)
   512→        if let requestedVoice, !requestedVoice.isEmpty, resolvedVoice == nil {
   513→            self.logger.warning("talk unknown voice alias \(requestedVoice, privacy: .public)")
   514→        }
   515→        if let voice = resolvedVoice {
   516→            if directive?.once == true {
   517→                self.logger.info("talk voice override (once) voiceId=\(voice, privacy: .public)")
   518→            } else {
   519→                self.currentVoiceId = voice
   520→                self.voiceOverrideActive = true
   521→                self.logger.info("talk voice override voiceId=\(voice, privacy: .public)")
   522→            }
   523→        }
   524→
   525→        if let model = directive?.modelId {
   526→            if directive?.once == true {
   527→                self.logger.info("talk model override (once) modelId=\(model, privacy: .public)")
   528→            } else {
   529→                self.currentModelId = model
   530→                self.modelOverrideActive = true
   531→            }
   532→        }
   533→
   534→        let apiKey = self.apiKey?.trimmingCharacters(in: .whitespacesAndNewlines)
   535→        let preferredVoice =
   536→            resolvedVoice ??
   537→            self.currentVoiceId ??
   538→            self.defaultVoiceId
   539→
   540→        let language = ElevenLabsTTSClient.validatedLanguage(directive?.language)
   541→
   542→        let voiceId: String? = if let apiKey, !apiKey.isEmpty {
   543→            await self.resolveVoiceId(preferred: preferredVoice, apiKey: apiKey)
   544→        } else {
   545→            nil
   546→        }
   547→
   548→        if apiKey?.isEmpty != false {
   549→            self.ttsLogger.warning("talk missing ELEVENLABS_API_KEY; falling back to system voice")
   550→        } else if voiceId == nil {
   551→            self.ttsLogger.warning("talk missing voiceId; falling back to system voice")
   552→        } else if let voiceId {
   553→            self.ttsLogger
   554→                .info(
   555→                    "talk TTS request voiceId=\(voiceId, privacy: .public) " +
   556→                        "chars=\(cleaned.count, privacy: .public)")
   557→        }
   558→        self.lastSpokenText = cleaned
   559→
   560→        let synthTimeoutSeconds = max(20.0, min(90.0, Double(cleaned.count) * 0.12))
   561→
   562→        guard self.isCurrent(gen) else { return nil }
   563→
   564→        return TalkPlaybackInput(
   565→            generation: gen,
   566→            cleanedText: cleaned,
   567→            directive: directive,
   568→            apiKey: apiKey,
   569→            voiceId: voiceId,
   570→            language: language,
   571→            synthTimeoutSeconds: synthTimeoutSeconds)
   572→    }
   573→
   574→    private func playElevenLabs(input: TalkPlaybackInput, apiKey: String, voiceId: String) async throws {
   575→        let desiredOutputFormat = input.directive?.outputFormat ?? self.defaultOutputFormat ?? "pcm_44100"
   576→        let outputFormat = ElevenLabsTTSClient.validatedOutputFormat(desiredOutputFormat)
   577→        if outputFormat == nil, !desiredOutputFormat.isEmpty {
   578→            self.logger
   579→                .warning(
   580→                    "talk output_format unsupported for local playback: " +
   581→                        "\(desiredOutputFormat, privacy: .public)")
   582→        }
   583→
   584→        let modelId = input.directive?.modelId ?? self.currentModelId ?? self.defaultModelId
   585→        func makeRequest(outputFormat: String?) -> ElevenLabsTTSRequest {
   586→            ElevenLabsTTSRequest(
   587→                text: input.cleanedText,
   588→                modelId: modelId,
   589→                outputFormat: outputFormat,
   590→                speed: TalkTTSValidation.resolveSpeed(
   591→                    speed: input.directive?.speed,
   592→                    rateWPM: input.directive?.rateWPM),
   593→                stability: TalkTTSValidation.validatedStability(
   594→                    input.directive?.stability,
   595→                    modelId: modelId),
   596→                similarity: TalkTTSValidation.validatedUnit(input.directive?.similarity),
   597→                style: TalkTTSValidation.validatedUnit(input.directive?.style),
   598→                speakerBoost: input.directive?.speakerBoost,
   599→                seed: TalkTTSValidation.validatedSeed(input.directive?.seed),
   600→                normalize: ElevenLabsTTSClient.validatedNormalize(input.directive?.normalize),
   601→                language: input.language,
   602→                latencyTier: TalkTTSValidation.validatedLatencyTier(input.directive?.latencyTier))
   603→        }
   604→
   605→        let request = makeRequest(outputFormat: outputFormat)
   606→        self.ttsLogger.info("talk TTS synth timeout=\(input.synthTimeoutSeconds, privacy: .public)s")
   607→        let baseUrl = self.ttsBaseUrl.flatMap { URL(string: $0) }
   608→        let client = baseUrl.map { ElevenLabsTTSClient(apiKey: apiKey, baseUrl: $0) }
   609→            ?? ElevenLabsTTSClient(apiKey: apiKey)
   610→        let stream = client.streamSynthesize(voiceId: voiceId, request: request)
   611→        guard self.isCurrent(input.generation) else { return }
   612→
   613→        if self.interruptOnSpeech {
   614→            guard await self.prepareForPlayback(generation: input.generation) else { return }
   615→        }
   616→
   617→        await MainActor.run { TalkModeController.shared.updatePhase(.speaking) }
   618→        self.phase = .speaking
   619→
   620→        let result = await self.playRemoteStream(
   621→            client: client,
   622→            voiceId: voiceId,
   623→            outputFormat: outputFormat,
   624→            makeRequest: makeRequest,
   625→            stream: stream)
   626→        self.ttsLogger
   627→            .info(
   628→                "talk audio result finished=\(result.finished, privacy: .public) " +
   629→                    "interruptedAt=\(String(describing: result.interruptedAt), privacy: .public)")
   630→        if !result.finished, result.interruptedAt == nil {
   631→            throw NSError(domain: "StreamingAudioPlayer", code: 1, userInfo: [
   632→                NSLocalizedDescriptionKey: "audio playback failed",
   633→            ])
   634→        }
   635→        if !result.finished, let interruptedAt = result.interruptedAt, self.phase == .speaking {
   636→            if self.interruptOnSpeech {
   637→                self.lastInterruptedAtSeconds = interruptedAt
   638→            }
   639→        }
   640→    }
   641→
   642→    private func playRemoteStream(
   643→        client: ElevenLabsTTSClient,
   644→        voiceId: String,
   645→        outputFormat: String?,
   646→        makeRequest: (String?) -> ElevenLabsTTSRequest,
   647→        stream: AsyncThrowingStream<Data, Error>) async -> StreamingPlaybackResult
   648→    {
   649→        let sampleRate = TalkTTSValidation.pcmSampleRate(from: outputFormat)
   650→        if let sampleRate {
   651→            self.lastPlaybackWasPCM = true
   652→            let result = await self.playPCM(stream: stream, sampleRate: sampleRate)
   653→            if result.finished || result.interruptedAt != nil {
   654→                return result
   655→            }
   656→            let mp3Format = ElevenLabsTTSClient.validatedOutputFormat("mp3_44100")
   657→            self.ttsLogger.warning("talk pcm playback failed; retrying mp3")
   658→            self.lastPlaybackWasPCM = false
   659→            let mp3Stream = client.streamSynthesize(
   660→                voiceId: voiceId,
   661→                request: makeRequest(mp3Format))
   662→            return await self.playMP3(stream: mp3Stream)
   663→        }
   664→        self.lastPlaybackWasPCM = false
   665→        return await self.playMP3(stream: stream)
   666→    }
   667→
   668→    private func playOpenAITTS(input: TalkPlaybackInput) async throws {
   669→        guard let baseUrlString = self.ttsBaseUrl, let baseUrl = URL(string: baseUrlString) else {
   670→            throw NSError(domain: "OpenAITTS", code: 1, userInfo: [
   671→                NSLocalizedDescriptionKey: "ttsBaseUrl not configured",
   672→            ])
   673→        }
   674→        let voice = self.currentVoiceId ?? self.defaultVoiceId ?? "default"
   675→        let model = self.currentModelId ?? self.defaultModelId ?? "qwen3-tts"
   676→        let sampleRate = 24000
   677→
   678→        self.ttsLogger.info(
   679→            "talk openai-tts start voice=\(voice, privacy: .public) " +
   680→                "model=\(model, privacy: .public) " +
   681→                "chars=\(input.cleanedText.count, privacy: .public)")
   682→
   683→        let stream = Self.openAITTSStream(
   684→            baseUrl: baseUrl,
   685→            text: input.cleanedText,
   686→            model: model,
   687→            voice: voice,
   688→            sampleRate: sampleRate,
   689→            timeoutSeconds: input.synthTimeoutSeconds)
   690→        guard self.isCurrent(input.generation) else { return }
   691→
   692→        if self.interruptOnSpeech {
   693→            guard await self.prepareForPlayback(generation: input.generation) else { return }
   694→        }
   695→
   696→        await MainActor.run { TalkModeController.shared.updatePhase(.speaking) }
   697→        self.phase = .speaking
   698→        self.lastPlaybackWasPCM = true
   699→
   700→        let finished = await Self.playInt16PCMStream(
   701→            stream: stream,
   702→            sampleRate: Double(sampleRate),
   703→            logger: self.ttsLogger)
   704→        self.ttsLogger.info("talk openai-tts finished=\(finished, privacy: .public)")
   705→        if !finished {
   706→            throw NSError(domain: "OpenAITTS", code: 1, userInfo: [
   707→                NSLocalizedDescriptionKey: "audio playback failed",
   708→            ])
   709→        }
   710→    }
