   712→    /// Plays a stream of int16 LE PCM bytes using AVAudioEngine with float32 format.
   713→    /// Returns true when playback completes normally.
   714→    @MainActor
   715→    private static func playInt16PCMStream(
   716→        stream: AsyncThrowingStream<Data, Error>,
   717→        sampleRate: Double,
   718→        logger: Logger) async -> Bool
   719→    {
   720→        guard let format = AVAudioFormat(
   721→            commonFormat: .pcmFormatFloat32,
   722→            sampleRate: sampleRate,
   723→            channels: 1,
   724→            interleaved: false)
   725→        else {
   726→            logger.error("talk openai-tts: failed to create AVAudioFormat")
   727→            return false
   728→        }
   729→
   730→        let engine = AVAudioEngine()
   731→        let player = AVAudioPlayerNode()
   732→        engine.attach(player)
   733→        engine.connect(player, to: engine.mainMixerNode, format: format)
   734→
   735→        do {
   736→            try engine.start()
   737→        } catch {
   738→            logger.error("talk openai-tts: engine start failed: \(error.localizedDescription, privacy: .public)")
   739→            return false
   740→        }
   741→
   742→        return await withCheckedContinuation { continuation in
   743→            var pendingBuffers = 0
   744→            var inputDone = false
   745→            var resumed = false
   746→
   747→            func finish(_ success: Bool) {
   748→                guard !resumed else { return }
   749→                resumed = true
   750→                player.stop()
   751→                engine.stop()
   752→                continuation.resume(returning: success)
   753→            }
   754→
   755→            Task { @MainActor in
   756→                do {
   757→                    for try await chunk in stream {
   758→                        guard chunk.count >= 2 else { continue }
   759→                        let frameCount = chunk.count / 2
   760→                        guard let buf = AVAudioPCMBuffer(
   761→                            pcmFormat: format,
   762→                            frameCapacity: AVAudioFrameCount(frameCount))
   763→                        else { continue }
   764→                        buf.frameLength = AVAudioFrameCount(frameCount)
   765→
   766→                        // Convert int16 LE → float32 [-1, 1]
   767→                        chunk.withUnsafeBytes { raw in
   768→                            let src = raw.bindMemory(to: Int16.self)
   769→                            let dst = buf.floatChannelData![0]
   770→                            for i in 0 ..< frameCount {
   771→                                dst[i] = Float(src[i]) / 32768.0
   772→                            }
   773→                        }
   774→
   775→                        pendingBuffers += 1
   776→                        player.scheduleBuffer(buf) {
   777→                            Task { @MainActor in
   778→                                pendingBuffers -= 1
   779→                                if inputDone, pendingBuffers == 0 {
   780→                                    finish(true)
   781→                                }
   782→                            }
   783→                        }
   784→                        if !player.isPlaying { player.play() }
   785→                    }
   786→                    inputDone = true
   787→                    if pendingBuffers == 0 { finish(true) }
   788→                } catch {
   789→                    logger.error("talk openai-tts stream error: \(error.localizedDescription, privacy: .public)")
   790→                    finish(false)
   791→                }
   792→            }
   793→        }
   794→    }
