   796→    private static func openAITTSStream(
   797→        baseUrl: URL,
   798→        text: String,
   799→        model: String,
   800→        voice: String,
   801→        sampleRate: Int,
   802→        timeoutSeconds: TimeInterval) -> AsyncThrowingStream<Data, Error>
   803→    {
   804→        AsyncThrowingStream { continuation in
   805→            let task = Task {
   806→                do {
   807→                    let url = baseUrl.appendingPathComponent("v1/audio/speech")
   808→                    let payload: [String: Any] = [
   809→                        "input": text,
   810→                        "model": model,
   811→                        "voice": voice,
   812→                        "response_format": "pcm",
   813→                        "sample_rate": sampleRate,
   814→                    ]
   815→                    let body = try JSONSerialization.data(withJSONObject: payload)
   816→
   817→                    var req = URLRequest(url: url)
   818→                    req.httpMethod = "POST"
   819→                    req.httpBody = body
   820→                    req.timeoutInterval = timeoutSeconds
   821→                    req.setValue("application/json", forHTTPHeaderField: "Content-Type")
   822→                    req.setValue("audio/pcm", forHTTPHeaderField: "Accept")
   823→
   824→                    // Use ephemeral session to bypass system-level internet reachability
   825→                    // checks that block LAN connections when there's no internet route.
   826→                    let sessionConfig = URLSessionConfiguration.ephemeral
   827→                    sessionConfig.timeoutIntervalForRequest = timeoutSeconds
   828→                    sessionConfig.timeoutIntervalForResource = timeoutSeconds + 30
   829→                    sessionConfig.waitsForConnectivity = false
   830→                    let session = URLSession(configuration: sessionConfig)
   831→                    let (bytes, response) = try await session.bytes(for: req)
   832→                    guard let http = response as? HTTPURLResponse else {
   833→                        throw NSError(domain: "OpenAITTS", code: 1, userInfo: [
   834→                            NSLocalizedDescriptionKey: "invalid response",
   835→                        ])
   836→                    }
   837→                    if http.statusCode >= 400 {
   838→                        var errorData = Data()
   839→                        for try await byte in bytes {
   840→                            errorData.append(byte)
   841→                            if errorData.count >= 4096 { break }
   842→                        }
   843→                        let msg = String(data: errorData.prefix(4096), encoding: .utf8) ?? "unknown"
   844→                        throw NSError(domain: "OpenAITTS", code: http.statusCode, userInfo: [
   845→                            NSLocalizedDescriptionKey: "OpenAI TTS failed: \(http.statusCode) \(msg)",
   846→                        ])
   847→                    }
   848→
   849→                    let chunkSize = 4096
   850→                    var buffer = Data()
   851→                    buffer.reserveCapacity(chunkSize)
   852→                    for try await byte in bytes {
   853→                        buffer.append(byte)
   854→                        if buffer.count >= chunkSize {
   855→                            continuation.yield(buffer)
   856→                            buffer.removeAll(keepingCapacity: true)
   857→                        }
   858→                    }
   859→                    if !buffer.isEmpty {
   860→                        continuation.yield(buffer)
   861→                    }
   862→                    continuation.finish()
   863→                } catch {
   864→                    continuation.finish(throwing: error)
   865→                }
   866→            }
   867→            continuation.onTermination = { _ in task.cancel() }
   868→        }
   869→    }
