   890→    private func resolveVoiceId(preferred: String?, apiKey: String) async -> String? {
   891→        let trimmed = preferred?.trimmingCharacters(in: .whitespacesAndNewlines) ?? ""
   892→        if !trimmed.isEmpty {
   893→            if let resolved = self.resolveVoiceAlias(trimmed) { return resolved }
   894→            self.ttsLogger.warning("talk unknown voice alias \(trimmed, privacy: .public)")
   895→        }
   896→        if let fallbackVoiceId { return fallbackVoiceId }
   897→
   898→        do {
   899→            let voices = try await ElevenLabsTTSClient(apiKey: apiKey).listVoices()
   900→            guard let first = voices.first else {
   901→                self.ttsLogger.error("elevenlabs voices list empty")
   902→                return nil
   903→            }
   904→            self.fallbackVoiceId = first.voiceId
   905→            if self.defaultVoiceId == nil {
   906→                self.defaultVoiceId = first.voiceId
   907→            }
   908→            if !self.voiceOverrideActive {
   909→                self.currentVoiceId = first.voiceId
   910→            }
   911→            let name = first.name ?? "unknown"
   912→            self.ttsLogger
   913→                .info("talk default voice selected \(name, privacy: .public) (\(first.voiceId, privacy: .public))")
   914→            return first.voiceId
   915→        } catch {
   916→            self.ttsLogger.error("elevenlabs list voices failed: \(error.localizedDescription, privacy: .public)")
   917→            return nil
   918→        }
   919→    }
   920→
   921→    private func resolveVoiceAlias(_ value: String?) -> String? {
   922→        let trimmed = (value ?? "").trimmingCharacters(in: .whitespacesAndNewlines)
   923→        guard !trimmed.isEmpty else { return nil }
   924→        let normalized = trimmed.lowercased()
   925→        if let mapped = self.voiceAliases[normalized] { return mapped }
   926→        if self.voiceAliases.values.contains(where: { $0.caseInsensitiveCompare(trimmed) == .orderedSame }) {
   927→            return trimmed
   928→        }
   929→        return Self.isLikelyVoiceId(trimmed) ? trimmed : nil
   930→    }
   931→
   932→    private static func isLikelyVoiceId(_ value: String) -> Bool {
   933→        guard value.count >= 10 else { return false }
   934→        return value.allSatisfy { $0.isLetter || $0.isNumber || $0 == "-" || $0 == "_" }
   935→    }
   936→
   937→    func stopSpeaking(reason: TalkStopReason) async {
   938→        let usePCM = self.lastPlaybackWasPCM
   939→        let interruptedAt = usePCM ? await self.stopPCM() : await self.stopMP3()
   940→        _ = usePCM ? await self.stopMP3() : await self.stopPCM()
   941→        await TalkSystemSpeechSynthesizer.shared.stop()
   942→        guard self.phase == .speaking else { return }
   943→        if reason == .speech, let interruptedAt {
   944→            self.lastInterruptedAtSeconds = interruptedAt
   945→        }
   946→        if reason == .manual {
   947→            return
   948→        }
   949→        if reason == .speech || reason == .userTap {
   950→            await self.startListening()
   951→            return
   952→        }
   953→        self.phase = .thinking
   954→        await MainActor.run { TalkModeController.shared.updatePhase(.thinking) }
   955→    }
   956→}
