  1104→    // MARK: - Audio level handling
  1105→
  1106→    private func noteAudioLevel(rms: Double) async {
  1107→        if self.phase != .listening, self.phase != .speaking { return }
  1108→        let alpha: Double = rms < self.noiseFloorRMS ? 0.08 : 0.01
  1109→        self.noiseFloorRMS = max(1e-7, self.noiseFloorRMS + (rms - self.noiseFloorRMS) * alpha)
  1110→
  1111→        let threshold = max(self.minSpeechRMS, self.noiseFloorRMS * self.speechBoostFactor)
  1112→        if rms >= threshold {
  1113→            let now = Date()
  1114→            self.lastHeard = now
  1115→            self.lastSpeechEnergyAt = now
  1116→        }
  1117→
  1118→        if self.phase == .listening {
  1119→            let clamped = min(1.0, max(0.0, rms / max(self.minSpeechRMS, threshold)))
  1120→            await MainActor.run { TalkModeController.shared.updateLevel(clamped) }
  1121→        }
  1122→    }
  1123→
  1124→    private static func rmsLevel(buffer: AVAudioPCMBuffer) -> Double? {
  1125→        guard let channelData = buffer.floatChannelData?.pointee else { return nil }
  1126→        let frameCount = Int(buffer.frameLength)
  1127→        guard frameCount > 0 else { return nil }
  1128→        var sum: Double = 0
  1129→        for i in 0..<frameCount {
  1130→            let sample = Double(channelData[i])
  1131→            sum += sample * sample
  1132→        }
  1133→        return sqrt(sum / Double(frameCount))
  1134→    }
