  1136→    private func shouldInterrupt(transcript: String, hasConfidence: Bool) async -> Bool {
  1137→        let trimmed = transcript.trimmingCharacters(in: .whitespacesAndNewlines)
  1138→        guard trimmed.count >= 3 else { return false }
  1139→        if self.isLikelyEcho(of: trimmed) { return false }
  1140→        let now = Date()
  1141→        if let lastSpeechEnergyAt, now.timeIntervalSince(lastSpeechEnergyAt) > 0.35 {
  1142→            return false
  1143→        }
  1144→        return hasConfidence
  1145→    }
  1146→
  1147→    private func isLikelyEcho(of transcript: String) -> Bool {
  1148→        guard let spoken = self.lastSpokenText?.lowercased(), !spoken.isEmpty else { return false }
  1149→        let probe = transcript.lowercased()
  1150→        if probe.count < 6 {
  1151→            return spoken.contains(probe)
  1152→        }
  1153→        return spoken.contains(probe)
  1154→    }
