    @MainActor
    private func captureEnrollmentAudio() async throws -> ([Float], Double) {
        let engine = AVAudioEngine()
        let input = engine.inputNode
        let format = input.outputFormat(forBus: 0)
        guard format.channelCount > 0, format.sampleRate > 0 else {
            throw NSError(
                domain: "TalkModeSettings",
                code: 1,
                userInfo: [NSLocalizedDescriptionKey: "No audio input available."])
        }

        let sampleRate = format.sampleRate
        let totalFrames = Int(sampleRate) * 10

        let countdownTask = Task { @MainActor in
            for remaining in stride(from: 9, through: 1, by: -1) {
                try? await Task.sleep(nanoseconds: 1_000_000_000)
                guard self.isEnrolling else { break }
                self.enrollTimeRemaining = remaining
            }
        }
        defer { countdownTask.cancel() }

        return try await withCheckedThrowingContinuation { continuation in
            nonisolated(unsafe) var collected = [Float]()
            collected.reserveCapacity(totalFrames)
            nonisolated(unsafe) var finished = false

            input.removeTap(onBus: 0)
            input.installTap(onBus: 0, bufferSize: 4096, format: format) { buffer, _ in
                guard !finished, let channel = buffer.floatChannelData?[0] else { return }
                let count = Int(buffer.frameLength)
                for i in 0..<count { collected.append(channel[i]) }

                let frameCount = min(count, 512)
                var sumSq: Float = 0
                for i in 0..<frameCount { sumSq += channel[i] * channel[i] }
                let rms = sqrt(sumSq / Float(max(frameCount, 1)))
                let level = min(1.0, Double(rms) * 20)
                Task { @MainActor in self.micLevel = level }

                if collected.count >= totalFrames {
                    finished = true
                    input.removeTap(onBus: 0)
                    engine.stop()
                    continuation.resume(returning: (Array(collected.prefix(totalFrames)), sampleRate))
                }
            }

            engine.prepare()
            do {
                try engine.start()
            } catch {
                input.removeTap(onBus: 0)
                continuation.resume(throwing: error)
            }
        }
    }
}

